async def get_mbti_result(message: Message, bot: Bot):
    result_mbti = {}
    result_mbti[message.from_user.id] = []
    if users_data[message.from_user.id][0] > 0:
        result_mbti[message.from_user.id].append('I')
    else:
        result_mbti[message.from_user.id].append('E')

    if users_data[message.from_user.id][1] > 0:
        result_mbti[message.from_user.id].append('N')
    else:
        result_mbti[message.from_user.id].append('S')

    if users_data[message.from_user.id][2] > 0:
        result_mbti[message.from_user.id].append('T')
    else:
        result_mbti[message.from_user.id].append('F')

    if users_data[message.from_user.id][3] > 0:
        result_mbti[message.from_user.id].append('J')
    else:
        result_mbti[message.from_user.id].append('P')

    time.sleep(1)
    deleter[message.from_user.id].insert(0, await bot.send_message(message.from_user.id, f'{person_text[''.join(result_mbti[message.from_user.id])]}'))
    deleter[message.from_user.id].insert(0, await bot.send_photo(message.from_user.id, f'{person_photos[''.join(result_mbti[message.from_user.id])]}', reply_markup=back_from_result()))
    await deleter[message.from_user.id][-1].delete()
    deleter[message.from_user.id].pop()




